  rect(0,size/2-d/2,size,d);//横白線 （x座標、ｙ座標、横幅、縦幅）
  rect(size/2-d/2,0,d,size);//縦白線　（x座標、ｙ座標、横幅、縦幅）
  // BLANK[2] (hint: 白い十字を描くには rect を二つ描こう)
}

